import Vue from 'vue'

import VueI18n from 'vue-i18n'

import ru from './ru.json'
import en from './en.json'

Vue.use(VueI18n)


const messages = {
    ru,
    en
}
const LocaleStorageLang = localStorage.getItem('lang')


const i18n = VueI18n.createI18n({
    locale: LocaleStorageLang,
    messages
})

export default i18n
